var map=document.getElementById("map");window.onload=function(){window.innerWidth>=991&&map.addEventListener("mousemove",function(e){var n=-(e.pageX+map.offsetLeft)/70,o=-(e.pageY+map.offsetTop)/70;map.style.backgroundPosition=n+"px "+o+"px"})},window.addEventListener("resize",function(){window.innerWidth>=991&&map.addEventListener("mousemove",function(e){var n=-(e.pageX+map.offsetLeft)/70,o=-(e.pageY+map.offsetTop)/70;map.style.backgroundPosition=n+"px "+o+"px"})});